sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
  var m=g_form.getValue('u_choice_3');
g_form.setDisplay('u_choice_2',false);
		g_form.setDisplay('u_choice_14',false);
		g_form.setDisplay('u_choice_15',false);
		g_form.setDisplay('u_choice_16',false);
		g_form.setDisplay('u_choice_17',false);
		g_form.setDisplay('u_choice_18',false);

	switch(m){
		case "1":

			g_form.setDisplay('u_choice_2',true);
			break;
		case "2":
                g_form.setDisplay('u_choice_14',true);
		g_form.setDisplay('u_choice_15',true);
		break;
               case "3":
                g_form.setDisplay('u_choice_14',true);
		g_form.setDisplay('u_choice_15',true);
		g_form.setDisplay('u_choice_16',true);
                   break;
              case "4":
                g_form.setDisplay('u_choice_14',true);
		g_form.setDisplay('u_choice_15',true);
		g_form.setDisplay('u_choice_16',true);
                g_form.setDisplay('u_choice_17',true);
                   break;
              case "5":
                g_form.setDisplay('u_choice_14',true);
		g_form.setDisplay('u_choice_15',true);
		g_form.setDisplay('u_choice_16',true);
                g_form.setDisplay('u_choice_17',true);
                g_form.setDisplay('u_choice_18',true);
                   break;
              case "6":
                    g_form.setDisplay('u_choice_2',true);
                     g_form.setDisplay('u_choice_14',true);
		g_form.setDisplay('u_choice_15',true);
		g_form.setDisplay('u_choice_16',true);
                g_form.setDisplay('u_choice_17',true);
                g_form.setDisplay('u_choice_18',true);
			break;
            default:
                 g_form.setDisplay('u_choice_2',true);
			      break;
}



}
