function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

  var n=g_form.getValue('u_choice_5');
                g_form.setDisplay('u_choice_6',false);
		g_form.setDisplay('u_choice_7',false);
		g_form.setDisplay('u_choice_8',false);
		g_form.setDisplay('u_choice_9',false);
		g_form.setDisplay('u_choice_10',false);
		g_form.setDisplay('u_choice_11',false);

	switch(n){
		case "1":

			g_form.setDisplay('u_choice_4',true);
			break;
		case "2":
                g_form.setDisplay('u_choice_4',true);
		g_form.setDisplay('u_choice_6',true);
		break;
               case "3":
                g_form.setDisplay('u_choice_4',true);
		g_form.setDisplay('u_choice_7',true);
		g_form.setDisplay('u_choice_6',true);
                   break;
              case "4":
                g_form.setDisplay('u_choice_4',true);
		g_form.setDisplay('u_choice_8',true);
		g_form.setDisplay('u_choice_6',true);
                g_form.setDisplay('u_choice_7',true);
                   break;
              case "5":
                g_form.setDisplay('u_choice_4',true);
		g_form.setDisplay('u_choice_9',true);
		g_form.setDisplay('u_choice_6',true);
                g_form.setDisplay('u_choice_7',true);
                g_form.setDisplay('u_choice_8',true);
                   break;
              case "6":
                    g_form.setDisplay('u_choice_10',true);
                     g_form.setDisplay('u_choice_4',true);
		g_form.setDisplay('u_choice_9',true);
		g_form.setDisplay('u_choice_6',true);
                g_form.setDisplay('u_choice_7',true);
                g_form.setDisplay('u_choice_8',true);
			break;
  case "7":
                    g_form.setDisplay('u_choice_10',true);
                     g_form.setDisplay('u_choice_4',true);
		g_form.setDisplay('u_choice_9',true);
		g_form.setDisplay('u_choice_6',true);
                g_form.setDisplay('u_choice_7',true);
                g_form.setDisplay('u_choice_8',true);
               g_form.setDisplay('u_choice_11',true);
			break;
            default:
                 g_form.setDisplay('u_choice_4',true);
			      break;
}


}
